import { AppointmentForm } from "../../components/appointmentForm/AppointmentForm";
import { TileList } from "../../components/tileList/TileList";

export const AppointmentsPage = ({appointment, setAppointment, contact, setContact, appointmentAddingFunction}) => {
  /*
  Define state variables for 
  appointment info
  */
 const [name, setName] = useState('');
 //const [contact, setContact] = useState('');
 const [date, setDate] = useState('');
 const [time, setTime] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    /*
    Add contact info and clear data  
    */
    setAppointment(prev => [...prev, appointmentObject]);
    setName('');
    setContact('');
    setDate('');
    setTime('');
  };

  return (
    <div>
      <section>
        <h2>Add Appointment</h2>
        <AppointmentForm name={name} contact={contact} date={date} time={time} onSubmit={handleSubmit} />
      </section>
      <hr />
      <section>
        <h2>Appointments</h2>
        <TileList value={appointment} />
      </section>
    </div>
  );
};
