import React, { useState, useEffect } from "react";

import { ContactForm } from "../../components/contactForm/ContactForm";
import { TileList } from "../../components/tileList/TileList";

export const ContactsPage = ({contact, setContact, contactObject}) => {
  /*
  Define state variables for 
  contact info and duplicate check
  */
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [duplicateName, setDuplicateName] = useState(false);

  useEffect(() => {
    for (let i = 0; i < name.length; i++) {
      for (let j = 0; j < name.length; j++) {
        if (name[i] === name[j]) {
          setDuplicateName(true);
        }  
      }
    }
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    /*
    Add contact info and clear data
    if the contact name is not a duplicate
    */
    if(duplicateName[duplicateName.length - 1] === false) {
      setContact(prev => [...prev, contactObject]);
      setName('');
      setPhone('');
      setEmail('');
    }
  };

  /*
  Using hooks, check for contact name in the 
  contacts array variable in props
  */

  return (
    <div>
      <section>
        <h2>Add Contact</h2>
        <ContactForm name={name} phone={phone} email={email} onSubmit={handleSubmit}/>  
      </section>
      <hr />
      <section>
        <h2>Contacts</h2>
        <TileList contact={contact}/>
      </section>
    </div>
  );
};
