import React from "react";
import { ContactPicker } from '../contactPicker/ContactPicker';

const getTodayString = () => {
  const [month, day, year] = new Date()
    .toLocaleDateString("en-US")
    .split("/");
  return `${year}-${month.padStart(2, "0")}-${day.padStart(2, "0")}`;
};

export const AppointmentForm = ({
  contacts,
  title,
  setTitle,
  contact,
  setContact,
  date,
  setDate,
  time,
  setTime,
  handleSubmit
}) => {
  function handleTitleChange({target}) {
    setTitle(target.value);
  }

  function handleDateChange({target}) {
    setDate(target.value);
  }

  function handleTimeChange({target}) {
    setTime(target.value);
  }

  function handleContactChange({target}) {
    setContact(target.value);
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <div>
        <div className="form">
          <label>
            <input id="title" type="text" value={title} onChange={handleTitleChange} />
          </label>
        </div>
        <div className="form">
          <label for="date" />
          <input id="date" type="date" value={date} min={getTodayString} onChange={handleDateChange} />
        </div>
        <div className="form">
          <label for="time" />
          <input id="time" type="time" value={time} onChange={handleTimeChange} />
        </div>
        <ContactPicker value={contact} onChange={handleContactChange} />
        <div className="form">
          <input type="submit" value="Submit" />
        </div>
        </div>
      </form>
    </>
  );
};
